import React from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";


// const basicElement = {id: '1', content: 'everega element'}

export default function ElementsList({elementLoyouts, loyouts, updateLoyouts}) {
  const [elements, setElements] = React.useState(loyouts);
  
  const [text, setText] = React.useState('hello');

  function handleChange(e) {
    setText(e.target.value);
  }

  

  const onDragEnd = (result) => {
    if (!result.destination) {
      return;
    }

    const items = Array.from(elements);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setElements(items);
  };


  function handleOnClick(){
    let items = Array.from(elements);
    let newId = (items.length + 1).toString();

    let newItem = {
      id: newId,
      content: "some cont",
    }

    items.push(newItem);

    console.log(newItem);

    setElements(items)
    updateLoyouts(items)
  }

  function addNewElement() {
    let items = Array.from(elements);
    let newId = (items.length + 1).toString();

    let newItem = {
      id: newId,
      content: text,
      left: 0,
      top: 0,
      zIndex: newId,
    }

    items.push(newItem);
    setElements(items)
  }

 let gagagaga= "gagagagag";

  return (
    <>
    <DragDropContext onDragEnd={onDragEnd}>
      <Droppable droppableId="elements">
        {(provided) => (
          <ul {...provided.droppableProps} ref={provided.innerRef}>
            {elements.map((element, index) => (
              <Draggable key={element.id} draggableId={element.id} index={index}>
                {(provided) => (
                  <li
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    // index меняется при перетягивании!!!
                    onClick={() => {console.log(index)}}
                  >
                    {element.content}
                  </li>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </ul>
        )}
      </Droppable>
    </DragDropContext>
    <button  onClick={handleOnClick}>click!!!</button>
                  
      <>
        <input value={text} onChange={handleChange} />
        <p>You typed: {text}</p>
        <button onClick={addNewElement}>
          add
        </button>
          {gagagaga}
      </>

    </>
  );
};
