import { useState } from "react"
import "./elementBlock.css"
import BorderBlock from "../borderBlocks/borderBlock";
import { elementBlockClassName } from "../../const/classNameConst";

export default function ElementBlock({childElement, identificator, onDragging, blockStyle, onResizeClick, onResizeClickUp, contentStyles, onSelectElement}) {
    // const [isSelected, selectElement] = useState(false)

    // function onSelectElement() {
    //     selectElement(true);
    // }

    if (isSelected) {
        return (
            <div className={elementBlockClassName} id = {identificator} style={blockStyle}  onMouseDown={(event) => {onDragging(identificator, event)}} >
                <BorderBlock></BorderBlock>

                <div className="content-element" style={contentStyles}>
                    {childElement.content}
                </div>

                <div className="drag-point left-up-point"></div>
                <div className="drag-point right-up-point" ></div>
                <div className="drag-point left-buttom-point"></div>
                <div className="drag-point right-buttom-point"></div>

            </div>
        )} 
        else {
            return (
            <div className="element-block" id = {identificator} style={blockStyle} >

                <div className="content-element" onMouseDown={onSelectElement} style={contentStyles}>
                    {childElement.content}
                </div>

                <div 
                // onMouseDown={(event) => {onResizeClick(event, identificator)}} 
                // onMouseUp={ (event) => {onResizeClickUp(event)}}
                className="drag-point left-up-point"></div>
                <div className="drag-point right-up-point" ></div>
                <div className="drag-point left-buttom-point" style={{left: "0", bottom: "0"}}></div>
                <div className="drag-point right-buttom-point" style={{right: "0", bottom: "0"}}></div>

            </div>
        )
    }
}
