import { useState } from 'react';
import ElementsList from '../elementList/elementsList';
import ElementBlock from '../elementBlock/elementBlock';

let initialElements = [
    { id: '1', content: 'Element 1', left: 0, top: 0, zIndex: 1. },
];
  

export default function Workspace() {
    const [elements, updateElements] = useState(initialElements);
    const [selectedElement, setSelectedElement] = useState(null);
    const worspace = document.getElementById('workspace')
    // сделать стэйт с выбранным элементом и менять его в блоке

    const onUpdateElements =(elements)=>{
        // let newElements = elements;
        // elements.join(element)
        updateElements(elements)
    }

    function selectElement(id) {
        setSelectedElement(id)
    }

    document.addEventListener('mousemove', (event) => {
        console.log(event.clientX)
    })
    

    return (
        <div id='workspace'>
            <div style={{background:'aqua', width:'200px', position:'absolute', right:'0'}}>
                <ElementsList elementLoyouts = {elements} onChange={onUpdateElements} />
            </div>

            <div id ='redactor'>
                
                {elements.map(({id, content, left, top, zIndex}) => {
                    return (
                        <ElementBlock identificator={id} onSelect={selectElement} position={{left, top}}>{content}</ElementBlock>
                    )
                })}    
                 
            </div>

            {/* <ElementBlock identificator={"replace"} onSelect={selectElement}></ElementBlock> */}
        </div>
    )
}
