import { useState } from "react"
import "./elementBlock.css"

export default function ElementBlock({childElement, identificator, onSelect, blockStyle, onResizeClick, onResizeClickUp, contentStyles}) {
    const [activeted, setActive] = useState(false);
    const borderStyles = {width:"100%", height: "100%", border: "3px", borderColor: "brown"}

    function onActivate() {
        console.log(activeted)
        setActive(!activeted);
    }

    return (
        <div className="element-block" id = {identificator} style={blockStyle} 
        onMouseDown={activeted ? (event) => {onSelect(identificator, event)} : console.log("not active")}
        >
            {activeted ? (<div className="border-div" styles={borderStyles}></div>) : (<></>)}
            

{/* onMouseDown={(event) => {onSelect(identificator, event)}} */}
            <div className="content-element" style={contentStyles} onMouseDown={onActivate}>
                {childElement.content}
            </div>

            <div 
            // onMouseDown={(event) => {onResizeClick(event, identificator)}} 
            // onMouseUp={ (event) => {onResizeClickUp(event)}}
            className="drag-point left-up-point"></div>
            <div className="drag-point right-up-point" ></div>
            <div className="drag-point left-buttom-point" style={{left: "0", bottom: "0"}}></div>
            <div className="drag-point right-buttom-point" style={{right: "0", bottom: "0"}}></div>

        </div>

    )
}
