import { useState, useRef, useEffect } from 'react';
import ElementsList from '../elementList/elementsList';
import ElementBlock from '../elementBlock/elementBlock';
import './workspace.css'
import Redactor from '../redactor/redactor';

let initialElements = [
    { id: '1', content: 'Element 1', left: 0, top: 0, zIndex: 1, },
];
  

export default function Workspace() {
    const [elements, updateElements] = useState(initialElements);
    
    const containerRef = useRef(null)
    // const boxRef = useRef(null) вместо этого будет список элементов  
    const elementsRef = useRef(null)
    
    //  может быть попытаться возврашать айди элемента и позицию клика, а потом менять позицию нажатого предмета. 
    
    let selectedElement = null;
    let elementCoord = {startX: 0, startY: 0, lastX: 0, lastY: 0, }
    let isClicked = false


    const onMouseUp = (event) => {
        isClicked = false;
        elementCoord.lastX = event.clientX;
        elementCoord.lastY = event.clientY;

        removeEventListener('mousemove', )
    }

    
    const onMouseMove = (event) => {
        if (!isClicked) return;
        
        const newElements = elements.map((obj) => {
            if (obj.id == selectedElement) {
                return {...obj, left: event.pageX, top: event.pageY};
            } 
            return obj;
        })
        updateElements(newElements)
    } 

    
    
    
    
    
    




    // const worspace = document.getElementById('workspace')
    // сделать стэйт с выбранным элементом и менять его в блоке

    const onUpdateElements =(elements)=>{
        // let newElements = elements;
        // elements.join(element)
        updateElements(elements)
    }

    function onMouseDown(id, x, y) {
        isClicked = true
        selectedElement = id
        elementCoord.startX = x
        elementCoord.startY = y


        window.addEventListener("mouseup", (event) => {onMouseUp(event)});
        window.addEventListener('mousemove', (event) => {onMouseMove(event)})
    }


    // document.addEventListener('mousemove', (event) => {
    //     let newElements = elements;
    //     newElements.map((element, index) => {
    //     })
    //     // console.log(event.clientX)
    // })
    

    return (
        <div id='workspace' >
            <div style={{background:'aqua', width:'200px', position:'absolute', right:'0'}}>
                <ElementsList elementLoyouts = {elements} updateLoyouts={onUpdateElements} />
            </div>

            <div id ='redactor'>                
                {elements.map((element, index) => {
                    let styles = {left : element.left,top: element.top, zIndex: element.zIndex}
                    return (
                        <ElementBlock key={element.id} identificator={element.id}  onSelect={onMouseDown} blockStyle={styles}>{element.content}</ElementBlock>
                    )
                })}    
            </div>
            

        </div>
    )
}
