import React from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

// interface skeletElement {
//   id: string;
//   content: string;
// }

let initialElements = [
  { id: '1', content: 'Element 1' },
  { id: '2', content: 'Element 2' },
  { id: '3', content: 'Element 3' },
];


// const basicElement = {id: '1', content: 'everega element'}

export default function ElementsList() {
  const [elements, setElements] = React.useState(initialElements);
  const [text, setText] = useState('hello');


  const onDragEnd = (result) => {
    if (!result.destination) {
      return;
    }

    const items = Array.from(elements);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setElements(items);
  };


  function handleOnClick(){
    let items = Array.from(elements);
    let newId = (items.length + 1).toString();

    let newItem = {
      id: newId,
      content: "some cont",
    }

    items.push(newItem);

    console.log(newItem);

    setElements(items)
  }

  function addNewElement(newContent) {
    let items = Array.from(elements);
    let newId = (items.length + 1).toString();

    let newItem = {
      id: newId,
      content: newContent,
    }

    items.push(newItem);

    console.log(newItem);

    setElements(items)
  }

  return (
    <>
    <DragDropContext onDragEnd={onDragEnd}>
      <Droppable droppableId="elements">
        {(provided) => (
          <ul {...provided.droppableProps} ref={provided.innerRef}>
            {elements.map((element, index) => (
              <Draggable key={element.id} draggableId={element.id} index={index}>
                {(provided) => (
                  <li
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                  >
                    {element.content}
                  </li>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </ul>
        )}
      </Droppable>
    </DragDropContext>
    <button onClick={handleOnClick}>click!!!</button>
                  
      <>
        <input value={text} onChange={handleChange} />
        <p>You typed: {text}</p>
        <button onClick={() => setText('hello')}>
          Reset
        </button>
      </>

    </>
  );
};
