    const [newCoords, setNewCoords] = useState({x: 0, y: 0});
    const [oldCoords, setOldCoords] = useState({x: 0, y: 0});
    const [isDrag, setIsDrag] = useState(false)
    
    let x = 0;
    let y = 0;

    function getX(dict){
