        <div className="element-block "
            onMouseDown={(event) => {setOldCoords({x: event.clientX, y: event.clientY}); x=newCoords.x; y=newCoords.y}} 
            onMouseUp={(event) => {setNewCoords({x: event.clientX, y: event.clientY})}}
            style={{left:{getX}, top:{y}}}
            >
            
            <div className="drag-point left-up-point" ></div>
