import { useEffect, useState } from "react"
import "./elementBlock.css"

export default function ElementBlock() {
    const [zIndex, setZ] = useState(0); 
    const [float, setFloat] = useState('center')//Этот параметр отвечает за позиционирование элементов внутри: по середине, слева, справа
    const [newCoords, setNewCoords] = useState({left: 0, top: 0});
    const [oldCoords, setOldCoords] = useState({x: 0, y: 0});
    const [isDrag, setIsDrag] = useState(false)
    let isDown = false;

    function getX(){
        return ( newCoords.x+'px' )
    }
    function getY(){
        return ( newCoords.y+'px' )
    }

    // document.addEventListener('mouseup', (event)=> {

    // })
    // } 
// TODO: реализовать перетягивание элемента. 

    document.addEventListener('mousedown', function(e) {
        setOldCoords({x: e.clientX, y: e.clientY})
        document.addEventListener('mousemove', onMouseDrag)
    }, true);

    document.addEventListener('mouseup', function() {
        document.removeEventListener('mousemove', onMouseDrag)
    }, true);

    document.addEventListener('mousemove', function(event) {
        event.preventDefault();
        if (isDown) {
            setNewCoords({ left : event.clientX, y : event.clientY})
        }
    }, true);

    function onMouseDrag({ movementX, movementY }) {
        setNewCoords({x: movementX, y: movementY})
    }

    return (
        <div className="element-block "
            // onMouseDown={(event) => {setOldCoords({x: event.clientX, y: event.clientY})}} 
            // onMouseUp={(event) => {setNewCoords({x: event.clientX, y: event.clientY})}}
            style={{left:getX(), top:getY()}}
            >
            
            <div className="drag-point left-up-point" ></div>
            <div className="drag-point right-up-point" ></div>
            <div className="drag-point left-buttom-point" style={{left: "0", bottom: "0"}}></div>
            <div className="drag-point right-buttom-point" style={{right: "0", bottom: "0"}}></div>
            {/* ({coords.x}, {coords.y}) */}
            {/* Вот я не знаю, как рендерить дочерние элементы. Они ведь могут быть разных типов, писать кучу условий? А может солид и ХЕшМАп? */}

        </div>

    )
}
