import { useState } from "react"
import "./elementBlock.css"
import { act } from "react-dom/test-utils";

export default function ElementBlock({childElement, identificator, onSelect, blockStyle, onResizeClick, onResizeClickUp, contentStyles, isSelected}) {
    
    const borderStyles = {width:"100%", height: "100%", border: "3px", borderColor: "brown"}



    if (isSelected) {
        return (
            <div className="element-block" id = {identificator} style={blockStyle} onMouseDown={(event) => {onSelect(identificator, event)}} >
                <div className="border-div" styles={borderStyles}></div>
                

    {/* onMouseDown={(event) => {onSelect(identificator, event)}} */}
                <div className="content-element" style={contentStyles}>
                    {childElement.content}
                </div>

                <div 
                // onMouseDown={(event) => {onResizeClick(event, identificator)}} 
                // onMouseUp={ (event) => {onResizeClickUp(event)}}
                className="drag-point left-up-point"></div>
                <div className="drag-point right-up-point" ></div>
                <div className="drag-point left-buttom-point" style={{left: "0", bottom: "0"}}></div>
                <div className="drag-point right-buttom-point" style={{right: "0", bottom: "0"}}></div>

            </div>
        )} 
        else {
            return (
            <div className="element-block" id = {identificator} style={blockStyle} >

                <div className="content-element" style={contentStyles} onMouseDown={(event) => {onSelect(identificator, event)}}>
                    {childElement.content}
                </div>

                <div 
                // onMouseDown={(event) => {onResizeClick(event, identificator)}} 
                // onMouseUp={ (event) => {onResizeClickUp(event)}}
                className="drag-point left-up-point"></div>
                <div className="drag-point right-up-point" ></div>
                <div className="drag-point left-buttom-point" style={{left: "0", bottom: "0"}}></div>
                <div className="drag-point right-buttom-point" style={{right: "0", bottom: "0"}}></div>

            </div>

        )
    }
}
