import React from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const initialElements = [
  { id: '1', content: 'Element 1' },
  { id: '2', content: 'Element 2' },
  { id: '3', content: 'Element 3' },
];

// Я НЕ ХОЧУ ЧТОБЫ ЭТА ХУИТА БЫЛА ТУТ, НО ОНА НЕ РАБОТАЕТ В ДРУГИХ МЕСТАХ ФФФФФФФФФФФАК
// наверное так происходит, потому что 
const ElementsList = () => {
  const [elements, setElements] = React.useState(initialElements);

  const onDragEnd = (result) => {
    if (!result.destination) {
      return;
    }

    const items = Array.from(elements);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setElements(items);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <Droppable droppableId="elements">
        {(provided) => (
          <ul {...provided.droppableProps} ref={provided.innerRef}>
            {elements.map((element, index) => (
              <Draggable key={element.id} draggableId={element.id} index={index}>
                {(provided) => (
                  <li
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                  >
                    {element.content}
                  </li>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </ul>
        )}
      </Droppable>
    </DragDropContext>
  );
};

const App = () => {
  return (
    <div>
      <h1>Draggable Elements List</h1>
      <ElementsList />
    </div>
  );
};

export default App;
