import { useEffect, useState } from "react"
import "./elementBlock.css"

export default function ElementBlock() {
    const [zIndex, setZ] = useState(0); 
    const [float, setFloat] = useState('center')//Этот параметр отвечает за позиционирование элементов внутри: по середине, слева, справа
    const [coords, setCoords] = useState({x: 0, y: 0});
    
    function handleChangeCoords(event) {
        setCoords({x: event.x, y: event.y})
    }

        
// TODO: реализовать перетягивание элемента. 

    return (
        <div className="element-block " onMouseMove={(event) => handleChangeCoords(event)}  onMouseUp={() => {console.log("больше не зажат")}} style={{left:coords.left , top: coords.top}}>
            <div className="drag-point left-up-point" ></div>
            <div className="drag-point right-up-point" ></div>
            <div className="drag-point left-buttom-point" style={{left: "0", bottom: "0"}}></div>
            <div className="drag-point right-buttom-point" style={{right: "0", bottom: "0"}}></div>
            ({coords.x}, {coords.y})
            {/* Вот я не знаю, как рендерить дочерние элементы. Они ведь могут быть разных типов, писать кучу условий? А может солид и ХЕшМАп? */}

        </div>

    )
}
