    //     if (!elementsRef.current || !containerRef.current) return;

    //     const boxes = elementsRef.current;
    //     const container = containerRef.current;

    //     const onMouseUp = (event) => {
    //         isClicked.current = false;
    //         elementCoord.current.lastX = event.clientX;
    //         elementCoord.current.lastY = event.clientY;
    //         console.log(elementCoord.current)
            
    //     }

    //     window.addEventListener("mouseup", onMouseUp);
    // }, [])
    

    const boxRef = useRef(null);
  
  
    const coords = useRef<{
      startX: number,
      startY: number,
      lastX: number,
      lastY: number
    }>({
      startX: 0,
      startY: 0,
      lastX: 0,
      lastY: 0
    })
  
    useEffect(() => {
      if (!boxRef.current || !containerRef.current) return;
  
      const box = boxRef.current;
      const container = containerRef.current;
  
  
      const onMouseDown = (e) => {
        isClicked.current = true;
        coords.current.startX = e.clientX;
        coords.current.startY = e.clientY;
      }
  
      const onMouseUp = (e) => {
        isClicked.current = false;
        coords.current.lastX = box.offsetLeft;
        coords.current.lastY = box.offsetTop;
      }
  
      const onMouseMove = (e) => {
        if (!isClicked.current) return;
  
        const nextX = e.clientX - coords.current.startX + coords.current.lastX;
        const nextY = e.clientY - coords.current.startY + coords.current.lastY;
  
        box.style.top = `${nextY}px`;
        box.style.left = `${nextX}px`;
      }
  
      box.addEventListener('mousedown', onMouseDown);
      box.addEventListener('mouseup', onMouseUp);
      container.addEventListener('mousemove', onMouseMove);
      container.addEventListener('mouseleave', onMouseUp);
  
      const cleanup = () => {
        box.removeEventListener('mousedown', onMouseDown);
        box.removeEventListener('mouseup', onMouseUp);
        container.removeEventListener('mousemove', onMouseMove);
        container.removeEventListener('mouseleave', onMouseUp);
      }
  
      return cleanup;
    }, [])
  

    const onMouseDown = (event) => {
        const selectedElement = event.target.id;
        console.log(selectedElement)
        isClicked.current = true;


        // const newElementsRef = elementsRef.map((obj) => {
        //     if (obj.id == selectedElement) {
        //         return {...obj, left: event.pageX, top: event.pageY};
        //     } 
        //     return obj;
        // })

    }
    
    
    
    
    
    
    




    // const worspace = document.getElementById('workspace')
    // сделать стэйт с выбранным элементом и менять его в блоке

    const onUpdateElements =(elements)=>{
        // let newElements = elements;
        // elements.join(element)
        updateElements(elements)
    }

    function selectElement(id, x, y) {
        selectedElement.current = id
        elementCoord.current.startX = x
        elementCoord.current.startY = y
    }

    // window.addEventListener('mouseup', (event) => {
    //     // setTimeout(() => {
    //         // console.log(event.target.id)
    //         const newElements = elements.map(obj => {
    //             if (obj.id == selectedElement) {
    //                 return {...obj, left: event.pageX, top: event.pageY};
    //             } 
    //             return obj;
    //         })
    //         // console.log(event.clientX)
    //         updateElements(newElements)
    //     // }, 100 )
    // })

    // function updateElementCoord(newCoords) {
        
    // }

    document.addEventListener('mousemove', (event) => {
        let newElements = elements;
        newElements.map((element, index) => {
        })
        // console.log(event.clientX)
    })
    

    return (
        <div id='workspace'>
            <div style={{background:'aqua', width:'200px', position:'absolute', right:'0'}}>
                <ElementsList elementLoyouts = {elements} updateLoyouts={onUpdateElements} />
            </div>

            <div id ='redactor'>                
                {elements.map((element, index) => {
                    let styles = {left : element.left,top: element.top, zIndex: element.zIndex}
                    return (
                        <ElementBlock key={element.id} identificator={element.id} handleClick={onMouseDown} onSelect={selectElement} blockStyle={styles}>{element.content}</ElementBlock>
                    )
                })}    
            </div>
            

        </div>
    )
}
