import "./elementBlock.css"
import { elementBlockClassName } from "../../const/classNameConst";
import { Resizable } from "react-resizable";
import BorderBlock from "../choosenElementMask/borderBlock";
import { useState } from "react";

export default function ElementBlock({childElement, identificator, onDragging, blockStyle, isSelected,  setS, contentStyles, onSelect}) {
    const width = blockStyle.width;
    const height = blockStyle.height;
    const [elementStyles,   setStyles ] = useState({
        blockStyle
      })

    const onResizeElement = (event, {node, size, handle}) => {
        const newStyles = {...elementStyles, width: size.width, height: size.height}

        setStyles(newStyles)
        setSize(identificator, {width: size.width, height: size.height})
    };



    function onSelectElement() {
        onSelect(identificator)
    }

    if (isSelected) {
        return (
        <Resizable  height={sizeElementState.height} width={sizeElementState.width} onResize={onResizeElement}>
            <div className={"element-block box"}  id = {identificator} style={blockStyle}  onMouseDown={(event) => {onDragging(identificator, event)}} >
            
                <div className="box "  style={contentStyles}>
                    {childElement.content}
                </div>

                <BorderBlock></BorderBlock>
                {/* <DragPoints></DragPoints>  */}
            {/* <div className="drag-point left-up-point" onClick={console.log("click resixe")}></div>
            <div className="drag-point right-up-point" ></div>
            <div className="drag-point left-buttom-point"></div>
            <div className="drag-point right-buttom-point"></div> */}

            </div>
        </Resizable>
        )} 
    else {
        return (
        <div className="element-block" id = {identificator} style={blockStyle} >

            <div className="content-element"
                onMouseDown={onSelectElement}
                style={contentStyles}>
                {childElement.content}
            </div>

        </div>
        )
    }
}
